//Адаптивное меню

let jsButtonMainMenu = document.querySelector('.jsButtonMainMenu');
let mainMenu = document.querySelector('.main-menu');

jsButtonMainMenu.addEventListener('click', () => {
    mainMenu.classList.toggle('jsMenuVisually');
});


//Preloader
let preloader = document.querySelector('.preloader');

function preloaderHidden() {
    preloader.style.visibility = 'hidden';
    preloader.style.opacity = '0';
};

setTimeout(preloaderHidden, 2000);

//Модальное окно
let jsLeftBarButton = document.querySelector('.jsLeftBarButton');
let modalFormWrapper = document.querySelector('.modal-form__wrapper');
let jsModalClose = document.querySelector('.jsModalClose');

if (jsLeftBarButton != null && modalFormWrapper != null && jsModalClose != null) {
    jsLeftBarButton.addEventListener('click', () => {
        modalFormWrapper.classList.toggle('jsVisually-hidden');
    });

    jsModalClose.addEventListener('click', () => {
        modalFormWrapper.classList.remove('jsVisually-hidden');
    });

     document.onclick = function (e) {
        if (e.target.className === 'modal-form__wrapper jsVisually-hidden') {
            modalFormWrapper.classList.remove('jsVisually-hidden');
        }
    };
}
//Video







//Консультация
var ConsultationBtns = document.querySelectorAll('.jsConsultationButton');
if (ConsultationBtns != null) {
    ConsultationBtns.forEach(function (btn) {
        btn.onclick = function () {
            var modal_cons = document.querySelector('.consultation-form__wrapper');
            modal_cons.classList.toggle('jsVisually-hidden');
        };
    });
}

var ConsultationClose = document.querySelectorAll('.jsConsultationClose');
if (ConsultationClose != null) {
    ConsultationClose.forEach(function (btn) {
        btn.onclick = function () {
             var modal_cons = document.querySelector('.consultation-form__wrapper');
             modal_cons.classList.remove('jsVisually-hidden');
        };
    });
}

//Абонементы
var BuyBtns = document.querySelectorAll('.jsBuyButton');
if (BuyBtns != null) {
    BuyBtns.forEach(function (btn) {
        btn.onclick = function () {
            var modal = document.querySelector('.buy-form__wrapper');
            var ValueBtn = btn.getAttribute("value");
            var HiddenInput = document.querySelector('.hidden-input');
            HiddenInput.setAttribute('value', ValueBtn);
            modal.classList.toggle('jsVisually-hidden');
        };
    });
}

var closeButton = document.querySelectorAll('.jsBuyClose');
if (closeButton != null) {

    closeButton.forEach(function (btn) {
      btn.onclick = function () {
            var modal = document.querySelector('.buy-form__wrapper');
            var HiddenInput = document.querySelector('.hidden-input');
            HiddenInput.removeAttribute('value');
            modal.classList.remove('jsVisually-hidden');
        };
    });
}
//Абонементы и Консультация при клике на пустую область
var modal = document.querySelector('.buy-form__wrapper');
var modal_cons = document.querySelector('.consultation-form__wrapper');

window.onclick = function (event) {
        if (event.target == modal_cons) {
            modal_cons.classList.remove('jsVisually-hidden');
        }
        if (event.target == modal) {
           modal.classList.remove('jsVisually-hidden');
        }
    };